import React, { useState, useEffect } from 'react';
import axios from "axios";
import Cookies from 'js-cookie';
import mainLogo from './logo.png';

const App = () => {
  const [value, setValue] = useState('');
  const [previousChat, setPreviousChats] = useState([]);

  useEffect(() => {
    // Check for a user cookie
    let userId = Cookies.get('userId');

    if (!userId) {
      // Create a new user and set a cookie
      // This assumes your backend has an endpoint to create a user and returns the user's ID
      axios.post('/api/add-user', {}).then(response => {
        userId = response.data.user_id; // Adjust depending on how your API returns the user ID
        Cookies.set('userId', userId, { expires: 365 }); // Set a cookie for 1 year
        // Optionally initialize chat or other user-specific data
      }).catch(error => console.error('Error creating user:', error));
    } else {
      // Fetch existing chat or data for the returning user
      // Adjust this according to how your backend manages threads or user data
      axios.get(`/api/get-user-thread/${userId}`).then(response => {
        const thread = response.data;
        setPreviousChats(thread.messages); // Adjust this according to your data structure
      }).catch(error => console.error('Error fetching user thread:', error));
    }
  }, []);

  // Your existing functions (reset, getMessages, checkStatus)...

  return (
    <div className="app">
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>The Friendly UBC Science Undergraduate Chatbot</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChat.map((chatMessage, index) => chatMessage.content && (
            <li key={index} className={`message ${chatMessage.role}`}>
              <p className='role'>{chatMessage.role}</p>
              <p>{chatMessage.content}</p>
            </li>
          ))}
        </ul>
        <div className="bottom-section">
          <div className="input-container">
            <input value={value} onChange={(e) => setValue(e.target.value)} />
            <div id="submit" onClick={() => {
              setPreviousChats(prevChats => [...prevChats, { role: "user", content: value }]);
              setValue('');
            }}>âž¢</div>
          </div>
          <p className="info"> Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
        </div>
      </section>
    </div>
  );
};

export default App;
