import React, { useState, useEffect } from 'react';
import axios from "axios";
import mainLogo from './logo.png';

const App = () => {
  const [value, setValue] = useState('');
  const [previousChat, setPreviousChats] = useState([]); // Assuming this is for a chat feature

  // Fetch users on component mount
  useEffect(() => {
    axios.get("/api/items")
      .then(response => setPreviousChats(response.data)) // Update the state with fetched users
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const reset = () => {
    setPreviousChats([]);
    setValue("");
  };

  const addUser = async () => {
    try {
      const userData = {
        user_id: Date.now(), 
        thread_id: 'thread_id_example', 
      };

      const response = await axios.post('/api/add-user', userData);
      console.log('User added:', response.data);
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  return (
    <div className="app">
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>The Friendly UBC Science Undergraduate Chatbot</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChat.map((user, index) => (
            <li key={index}>
              <p>User ID: {user.user_id}</p>
              <p>Thread ID: {user.thread_id}</p>
            </li>
          ))}
        </ul>
        <div className="bottom-section">
          <div className="input-container">
            <button onClick={reset}>Reset</button>
            <button onClick={addUser}>Add User</button>
            <input value={value} onChange={(e) => setValue(e.target.value)} />
          </div>
          <p className="info"> Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
        </div>
      </section>
    </div>
  );
};

export default App;
