import React, { useState, useEffect, useRef } from 'react';
import axios from "axios";
import mainLogo from './logo.png';
import Cookies from 'js-cookie';
import OpenAI from "openai";


const API_KEY = "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"

const openai = new OpenAI({apiKey: API_KEY,
  dangerouslyAllowBrowser: true});


const App = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [isLoading, setIsLoading] = useState(false);
  const [value, setValue] = useState('');
  const [previousChats, setPreviousChats] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [previousChats]);

  const reset = async () => {
    console.log("Reset button clicked");
    const threadId = Cookies.get('threadId');
    if (threadId) {
      try {
        await axios.post(`/api/threads/${threadId}/reset`);
        setPreviousChats([]);
        setValue("");
      } catch (error) {
        console.error('Error resetting the thread:', error);
      }
    }
  };

  useEffect(() => {
    const fetchThreadMessages = async (threadId) => {
      try {
        const response = await axios.get(`/api/threads/${threadId}`);
        const { messages } = response.data;
        if (messages && messages.length > 0) {
          setPreviousChats(messages.map(message => ({
            role: message.role,
            content: message.content
          })));
        }
      } catch (error) {
        console.error('Error fetching thread messages:', error);
      }
    };

    let threadId = Cookies.get('threadId');

    if (threadId) {
      fetchThreadMessages(threadId);
    } else {
      createNewThread().then((newThreadId) => {
        Cookies.set('threadId', newThreadId, { expires: 365 });
        setPreviousChats([]);
      }).catch(error => console.error('Error creating thread:', error));
    }
  }, []);

  const createNewThread = async () => {
    try {
      const thread = await openai.beta.threads.create();
      await axios.post('/api/create-thread', { thread_id: thread.id });
      Cookies.set('threadId', thread.id, { expires: 365 });
      return thread.id;
    } catch (error) {
      console.error('Error creating thread or communicating with backend:', error);
    }
  };

  const getMessages = async (event) => {

    if (event) {
      event.preventDefault();
    }

    if (!value || isLoading) return;
    setIsLoading(true); 
    const threadId = Cookies.get('threadId');
    if (!threadId) {
      console.error('No thread ID found.');
      setIsLoading(false);
      return;
    }
    const userMessage = { role: 'user', content: value };
    setPreviousChats(prevChats => [...prevChats, userMessage]);
    setValue("");
    try {
      await axios.post(`/api/threads/${threadId}/add-message`, userMessage);

      const assistant = await openai.beta.assistants.retrieve("asst_9baNgmXjR1e9tFitiE9AYewq");
      await openai.beta.threads.messages.create(threadId, { role: "user", content: value });

      const run = await openai.beta.threads.runs.create(threadId, { assistant_id: assistant.id });
      await checkStatus(threadId, run.id);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
    setIsLoading(false);
  };


  async function checkStatus(threadId, runId) {
    let isComplete = false;
    while (!isComplete) {
      const runStatus = await openai.beta.threads.runs.retrieve(threadId, runId);
      if (runStatus.status === "completed") {
        isComplete = true;
        const messages = await openai.beta.threads.messages.list(threadId);
        console.log(messages)
        const assistantMessage = {
          role: 'assistant',
          content: messages.data[0].content[0].text.value
        };
        await axios.post(`/api/threads/${threadId}/add-message`, assistantMessage);

        setPreviousChats(prevChats => [
          ...prevChats,
          assistantMessage
        ]);
      } else {
        await new Promise((resolve) => setTimeout(resolve, 2000));
      }
    }
  }

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => window.removeEventListener('resize', handleResize);
  }, []);


  return (
    <div className="app">
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>{windowWidth <= 600 ? 'UBC Science Undergraduate Chatbot' : 'The Friendly UBC Science Undergraduate Chatbot'}</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChats.map((chat, index) => (
            <li key={index} className={`message ${chat.role}`}>
              <p className='role'>{chat.role}</p>
              <p>{chat.content}</p>
              {index === previousChats.length - 1 ? <div ref={messagesEndRef} /> : null}
            </li>
          ))}
        </ul>
        <div className="bottom-section">
        <div className="input-container">
            <button onClick={reset} disabled={isLoading}>Reset</button>
            <input
              value={value}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {  
                  e.preventDefault();  
                  getMessages();  
                }
              }}
              onChange={(e) => setValue(e.target.value)}
              placeholder={isLoading ? "Thinking..." : ""}
              disabled={isLoading}
            />
            {isLoading ? (
              <div className="spinner" style={{ opacity: isLoading ? 0.5 : 1 }}>Loading...</div>
            ) : (
              <div id="submit" onClick={getMessages} style={{ opacity: isLoading ? 0.5 : 1 }}>âž¢</div>
            )}
          </div>
          <p className="info"> Please note that the information provided by this chatbot is based on available data and algorithms, and may not always be accurate. We recommend double-checking any critical information to ensure its reliability and accuracy. </p>
        </div>
      </section>
    </div>
  );
};

export default App;
