import React, { useState, useEffect } from 'react';
import axios from "axios";
import mainLogo from './logo.png';
import Cookies from 'js-cookie';

const App = () => {
  const [value, setValue] = useState('');
  const [previousChats, setPreviousChats] = useState([]);

  const reset = () => {
    setPreviousChats([]);
    setValue("");
  };

  useEffect(() => {
    let userId = Cookies.get('userId');

    if (!userId) {
      // If no userId, create a new user and set a cookie
      axios.post('/api/add-user').then(response => {
        userId = response.data.user_id;
        Cookies.set('userId', userId, { expires: 365 });
        // No previous chats for a new user
        setPreviousChats([]);
      }).catch(error => console.error('Error creating user:', error));
    } else {
      // Fetch previous chats for the returning user
      axios.get(`/api/get-user-thread/${userId}`).then(response => {
        setPreviousChats(Array.isArray(response.data) ? response.data : []);
      }).catch(error => console.error('Error fetching user thread:', error));
    }
  }, []);

  const handleSubmit = async () => {
    if (!value) return;
    const newMessage = { role: 'user', content: value };
    setPreviousChats([...previousChats, newMessage]);

    // Here, ideally, you should also send this new message to the backend to be saved
    // axios.post('/api/save-message', { userId, message: newMessage });

    setValue(""); 
  };

  return (
    <div className="app">
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>The Friendly UBC Science Undergraduate Chatbot</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChats.map((chat, index) => (
            <li key={index} className={`message ${chat.role}`}>
              <p className='role'>{chat.role}</p>
              <p>{chat.content}</p>
            </li>
          ))}
        </ul>
        <div className="bottom-section">
          <div className="input-container">
            <button onClick={reset}>Reset</button>
            <input value={value} onChange={(e) => setValue(e.target.value)} />
            <div id="submit" onClick={handleSubmit}>âž¢</div>
          </div>
          <p className="info"> Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
        </div>
      </section>
    </div>
  );
};

export default App;
