import React, { useState, useEffect, useRef } from 'react';
import axios from "axios";
import mainLogo from './logo.png';
import info from './info.png';
import deletebutton from './delete.png';
import Cookies from 'js-cookie';
import OpenAI from "openai";

const API_KEY = "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"
let apiKey = '';

const openai = new OpenAI({dangerouslyAllowBrowser: true});

  const WelcomeModal = ({ onClose }) => (
    <div className="welcome-overlay" onClick={onClose}>
      <div className="welcome-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-section">
          <h3>Examples</h3>
          <p>"What are prerequisite courses for CPSC 340?"</p>
          <p>"What courses should I take if I want to learn about cell biology?"</p>
          <p>"What are some 300 level COGS courses?"</p>
        </div>

        <div className="modal-section">
          <h3>Capabilities</h3>
          <p>Remembers what user said earlier in the conversation</p>
          <p>Allows user to provide follow-up corrections</p>
          <p>Trained to decline inappropriate requests</p>
        </div>

        <div className="modal-section">
          <h3>Limitations</h3>
          <p>May occasionally generate incorrect information</p>
          <p>May occasionally produce harmful instructions or biased content</p>
          <p>Has limited knowledge specific to UBC</p>
        </div>

        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );

const App = () => {
  const [isFirstVisit, setIsFirstVisit] = useState(false);
  const [showWelcome, setShowWelcome] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [isLoading, setIsLoading] = useState(false);
  const [value, setValue] = useState('');
  const [previousChats, setPreviousChats] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    const fetchApiKey = async () => {
      try {
        const response = await axios.get('/api/api-key');
        console.log(response.data.apiKey)
        openai.config.apiKey = response.data.apiKey;
        console.log('API Key:', response.data.apiKey);
      } catch (error) {
        console.error('Error fetching API key:', error);
      }
    };

    fetchApiKey();
  }, []);


  useEffect(() => {
    const hasVisited = localStorage.getItem('hasVisited');
    if (!hasVisited) {
      setIsFirstVisit(true);
      localStorage.setItem('hasVisited', 'true');
    }
  }, []);


  useEffect(() => {
    scrollToBottom();
  }, [previousChats]);

  const reset = async () => {
    let threadId = Cookies.get('threadId');
    if (threadId) {
      try {
        Cookies.remove('threadId');
        setPreviousChats([]);
        setValue("");
        await axios.post(`/api/threads/${threadId}/reset`);
      } catch (error) {
        console.error('Error resetting the thread:', error.response ? error.response.data : error);
      }
    }
  };

  useEffect(() => {
    const fetchThreadMessages = async (threadId) => {
      try {
        const response = await axios.get(`/api/threads/${threadId}`);
        const { messages } = response.data;
        if (messages && messages.length > 0) {
          setPreviousChats(messages.map(message => ({
            role: message.role,
            content: message.content
          })));
        }
      } catch (error) {
        console.error('Error fetching thread messages:', error);
      }
    };

    let threadId = Cookies.get('threadId');

    if (threadId) {
      fetchThreadMessages(threadId);
    } else {
      createNewThread().then((newThreadId) => {
        Cookies.set('threadId', newThreadId, { expires: 365 });
        console.log(newThreadId)
        getMessages();
      }).catch(error => console.error('Error creating thread:', error));
    }
  }, []);

  const createNewThread = async () => {
    try {
      const thread = await openai.beta.threads.create();
      await axios.post('/api/create-thread', { thread_id: thread.id });
      Cookies.set('threadId', thread.id, { expires: 365 });
      return thread.id;
    } catch (error) {
      console.error('Error creating thread or communicating with backend:', error);
    }
  };

  const getMessages = async (event) => {

    if (event) {
      event.preventDefault();
    }

    if (!value || isLoading) return;
    setIsLoading(true); 
    let threadId = Cookies.get('threadId');
    if (!threadId) {
      try {
        const response = await createNewThread();
        threadId = response;
        Cookies.set('threadId', threadId, { expires: 365 });
      } catch (error) {
        console.error('Error creating thread:', error);
        setIsLoading(false);
        return;
      }
    }
    const userMessage = { role: 'user', content: value };
    setPreviousChats(prevChats => [...prevChats, userMessage]);
    setValue("");
    try {
      await axios.post(`/api/threads/${threadId}/add-message`, userMessage);

      const assistant = await openai.beta.assistants.retrieve("asst_9baNgmXjR1e9tFitiE9AYewq");
      await openai.beta.threads.messages.create(threadId, { role: "user", content: value });

      const run = await openai.beta.threads.runs.create(threadId, { assistant_id: assistant.id });
      await checkStatus(threadId, run.id);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
    setIsLoading(false);
  };


  async function checkStatus(threadId, runId) {
    let isComplete = false;
    while (!isComplete) {
      const runStatus = await openai.beta.threads.runs.retrieve(threadId, runId);
      if (runStatus.status === "completed") {
        isComplete = true;
        const messages = await openai.beta.threads.messages.list(threadId);
        console.log(messages)
        const assistantMessage = {
          role: 'assistant',
          content: messages.data[0].content[0].text.value
        };
        await axios.post(`/api/threads/${threadId}/add-message`, assistantMessage);

        setPreviousChats(prevChats => [
          ...prevChats,
          assistantMessage
        ]);
      } else {
        await new Promise((resolve) => setTimeout(resolve, 2000));
      }
    }
  }
  const toggleWelcomeModal = () => {
    setShowWelcome(!showWelcome);
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => window.removeEventListener('resize', handleResize);
  }, []);


  return (
    <div className="app">
       <button onClick={toggleWelcomeModal} className="info-button">
        <img src={info} height={30}></img>
       </button>
      {showWelcome && <WelcomeModal onClose={toggleWelcomeModal} />}
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>{windowWidth <= 600 ? 'UBC Science Undergraduate Chatbot' : 'The Friendly UBC Science Undergraduate Chatbot'}</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChats.map((chat, index) => (
            <li key={index} className={`message ${chat.role}`}>
              <p className='role'>{chat.role}</p>
              <p>{chat.content}</p>
              {index === previousChats.length - 1 ? <div ref={messagesEndRef} /> : null}
            </li>
          ))}
        </ul>
        <div className="bottom-section">
        <div className="input-container">
            <button onClick={reset} disabled={isLoading}>
            <img src={deletebutton} height={25} alt="Delete Icon" />
            </button>
            <input
              value={value}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {  
                  e.preventDefault();  
                  getMessages();  
                }
              }}
              onChange={(e) => setValue(e.target.value)}
              placeholder={isLoading ? "Thinking..." : "Type your message here"}
              disabled={isLoading}
            />
            {isLoading ? (
              <div class="lds-ring"><div></div><div></div><div></div><div></div></div>
            ) : (
              <div id="submit" onClick={getMessages} style={{ opacity: isLoading ? 0.5 : 1 }}>âž¢</div>
            )}
          </div>
          <p className="info"> Please note that the information provided by this chatbot is based on available data and algorithms, and may not always be accurate. We recommend double-checking any critical information to ensure its reliability and accuracy. </p>
        </div>
      </section>
    </div>
  );
};

export default App;
