import React, { useState, useEffect } from 'react';
import mainLogo from './logo.png';
import Cookies from 'js-cookie';
import OpenAI from "openai";
import axios from "axios";
import Blog from "./model/Blog.js"

const API_KEY = "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"


const App = () => {
  const [message, setMessage] = useState(null)
  const [value, setValue] = useState(null)
  const openai = new OpenAI({apiKey: API_KEY,
  dangerouslyAllowBrowser: true});
  const [items, setItems] = useState([]);

  const [previousChat, setPreviousChats] = useState([])

  const article = new Blog({
    title: 'Awesome Post!',
    slug: 'awesome-post',
    published: true,
    content: 'This is the best post ever',
    tags: ['featured', 'announcement'],
  });

  const reset = () => {
    setPreviousChats([])
    setMessage(null);
    setValue("")
  }


  const getMessages = async () => {
    try {
      const assistant = await openai.beta.assistants.retrieve("asst_9baNgmXjR1e9tFitiE9AYewq");
      const thread = await openai.beta.threads.create();
      await openai.beta.threads.messages.create(
        thread.id,
        {
          role: "user",
          content: value
        }
      );

      setMessage(value)

      const run = await openai.beta.threads.runs.create(
        thread.id,
        { 
          assistant_id: assistant.id
        }
      );

      await checkStatus(thread.id, run.id);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  }

  async function checkStatus(threadId, runId) {
    let isComplete = false;
    while (!isComplete) {
      const runStatus = await openai.beta.threads.runs.retrieve(threadId, runId);
      if (runStatus.status === "completed") {
        isComplete = true;
        const messages = await openai.beta.threads.messages.list(threadId);
        messages.data.forEach((msg) => {
          if (msg.role === 'assistant' && msg.content && msg.content.length > 0 && msg.content[0].text && msg.content[0].text.value) {
            const assistantResponse = msg.content[0].text.value;
            setPreviousChats(prevChats => [
              ...prevChats,
              { role: 'assistant', content: assistantResponse }
            ]);
          }
        });
      } else {
        await new Promise((resolve) => setTimeout(resolve, 2000));
      }
    }
  }

  useEffect(() => {
    if (message && value){
      setPreviousChats(prevChats => (
        [...prevChats,
        {
          role: "user",
          content: value
        },
        {
          role: message.role,
          content: message.content
        }
      ]
      ))
    }
  }, [message])

  const addBlog = async () => {
    try {
      const blogData = {
        title: 'New Blog Post',
        slug: 'new-blog-post',
        published: true,
        content: 'This is the content of the blog post.',
        tags: ['new', 'featured']
      };

      const response = await axios.post('/api/add-blog', blogData);
      console.log('Blog added:', response.data);
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Error data:', error.response.data);
        console.error('Error status:', error.response.status);
        console.error('Error headers:', error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('Error request:', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error message:', error.message);
      }
      console.error('Error config:', error.config);
    }
  };


  return (
    <div className="app">
      <div className='top-section'>
      <img src={mainLogo} height={75}></img>
      <h3>The Frindly UBC Science Undergraduate Chatbot</h3>
      </div>
      <section className="main">
      <ul className='feed'>
        {previousChat.map((chatMessage, index) => chatMessage.content && (
          <li key={index} className={`message ${chatMessage.role}`}>
            <p className='role'>{chatMessage.role}</p>
            <p>{chatMessage.content}</p>
          </li>
        ))}
      </ul>

      <div className="bottom-section">

      <div className="input-container">
        <button onClick={reset}>Reset</button>
        <button onClick={addBlog}>blogSchema</button>
        <input value = {value} onChange={(e) => setValue(e.target.value)}/>
        <div id="submit" onClick={getMessages} >➢</div>
      </div>
          <p className="info"> Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
      </div>
      </section>
    </div>
  )
}


export default App
