import React, { useState, useEffect } from 'react';
import axios from "axios";
import mainLogo from './logo.png';

const App = () => {
  const [value, setValue] = useState('');
  const [previousChat, setPreviousChats] = useState([]);

  // Fetch items (blogs) on component mount
  useEffect(() => {
    axios.get("/api/items")
      .then(response => setPreviousChats(response.data))
      .catch(error => console.error('Error fetching items:', error));
  }, []);

  const reset = () => {
    setPreviousChats([]);
    setValue("");
  };

  const addBlog = async () => {
    try {
      const blogData = {
        title: 'New Blog Post',
        slug: 'new-blog-post',
        published: true,
        content: 'This is the content of the blog post.',
        tags: ['new', 'featured']
      };

      const response = await axios.post('/api/add-blog', blogData);
      console.log('Blog added:', response.data);
      // Update the UI accordingly
      // For example, you can fetch the blogs again or just append the new blog to previousChat
    } catch (error) {
      console.error('Error adding blog:', error);
    }
  };

  return (
    <div className="app">
      <div className='top-section'>
        <img src={mainLogo} alt="Main Logo" height={75} />
        <h3>The Friendly UBC Science Undergraduate Chatbot</h3>
      </div>
      <section className="main">
        <ul className='feed'>
          {previousChat.map((chatMessage, index) => (
            <li key={index} className={`message ${chatMessage.role}`}>
              <p className='role'>{chatMessage.role}</p>
              <p>{chatMessage.content}</p>
            </li>
          ))}
        </ul>
        <div className="bottom-section">
          <div className="input-container">
            <button onClick={reset}>Reset</button>
            <button onClick={addBlog}>Add Blog</button>
            <input value={value} onChange={(e) => setValue(e.target.value)} />
            {/* Assuming you have a mechanism to submit the message */}
          </div>
          <p className="info"> Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
        </div>
      </section>
    </div>
  );
}

export default App;
